import streams from '../apis/streams';
import {
  SIGN_IN,
  SIGN_OUT,
  CREATE_STREAM,
  FETCH_STREAMS,
  FETCH_STREAM,
  DELETE_STREAM,
  EDIT_STREAM,
} from './types';

export const signIn = (userId) => {
  return { type: SIGN_IN, paylod: userId };
};

export const signOut = () => {
  return { type: SIGN_OUT };
};

export const createStream = (formValues) => async (dispatch) => {
  const response = await streams.post('/streams', formValues);
  dispatch({ type: CREATE_STREAM, payload: response.data });
};

export const fetchStreams = () => async (dispatch) => {
  const response = await streams.get('/streams');
  dispatch({ type: CREATE_STREAM, payload: response.data });
};

export const fetchStream = (id) => async (dispatch) => {
  const reponse = await streams.get(`/streams/${id}`);
  dispatch({ type: FETCH_STREAM, payload: reponse.data });
};

export const editStream = (id, formValue) => async (dispatch) => {
  const reponse = await streams.put(`/streams/${id}`, formValue);
};
